INSERT INTO `faqs` (`id`, `question`, `answer`, `page_id`, `published`, `created_at`, `updated_at`) VALUES
	(1, 'Seramik Kaplama mı yoksa PPF Kaplama mı yaptırmalıyım? Farkı nedir?', 'Bu karar aracınızı neye karşı korumak istediğinize bağlıdır. <strong>Seramik Kaplama</strong>; kimyasal koruma sağlar (kuş pisliği, asit yağmuru, güneş yanığı) ve araca yüksek parlaklık, kolay temizlenme özelliği katar. Ancak taş vuruklarına karşı koruma sağlamaz.\r\nPPF (Boya Koruma Filmi) ise fiziksel bir zırhtır; taş sekmelerine, derin çiziklere ve sürtmelere karşı %100 koruma sağlar. Eğer uzun yol yapıyorsanız PPF, şehir içi parlaklık istiyorsanız Seramik öneririz.', NULL, 1, '2025-11-25 21:52:40', '2025-11-25 21:52:40'),
	(2, 'Seramik kaplamanın ömrü ne kadardır? Garantili mi?', 'Seramik kaplamanın ömrü, seçilen ürünün kalitesine (H sertlik derecesi) ve kullanım koşullarına göre değişir. Sirius Car Design olarak uyguladığımız profesyonel seramik paketlerimiz 2 yıl ile 5 yıl arasında koruma sağlar. Bu sürenin korunması için aracın fırçasız, pH nötr şampuanlarla yıkanması önemlidir.', NULL, 1, '2025-11-25 21:53:58', '2025-11-25 21:53:58'),
	(3, 'Cam filmi uygulaması yasak mı? Muayeneden geçer mi?', 'Karayolları Trafik Yönetmeliği\'ne göre, sürücünün görüşünü engellemeyen belirli koyuluk oranlarına izin verilmektedir. Uyguladığımız yasal sınırlara uygun (açık ve orta ton) cam filmleri, içeriden dışarıyı net gösterirken ısıyı keser ve araç muayenesinden (TÜVTÜRK) sorunsuz geçer. Ön cama (windshield) ise sadece şeffaf ısı kontrol filmleri uygulanabilir.', NULL, 1, '2025-11-25 21:54:48', '2025-11-25 21:54:48'),
	(4, 'PPF Kaplama sökülürken aracın boyasına zarar verir mi?', 'Hayır, vermez. Ancak burada en önemli faktör kullanılan malzemedir. Eğer kaliteli TPU (Termoplastik Poliüretan) film kullanılmışsa, söküldüğünde boya kalkmaz ve yapışkan izi kalmaz. Kalitesiz PVC filmler zamanla boyayla bütünleşip zarar verebilir. Biz Sirius\'ta sadece boya dostu, garantili TPU filmler kullanıyoruz.', NULL, 1, '2025-11-25 21:55:18', '2025-11-25 21:55:18'),
	(5, 'Detaylı iç temizlik ve sterilizasyon ne kadar sürer?', 'Standart bir yıkamanın aksine, detaylı temizlik (oto kuaför) işlemi titizlik gerektirir. Koltukların yıkanması, tavan temizliği, taban halısı ve kurutma işlemleri dahil olmak üzere aracınızın durumuna göre ortalama 1 iş günü sürmektedir. Aracınızı tam kurumuş ve ozonla dezenfekte edilmiş şekilde teslim ediyoruz.', NULL, 1, '2025-11-25 21:55:41', '2025-11-25 21:55:41'),
	(6, 'Jant boyama işlemi sonrası boya atması olur mu?', 'Hayır. Jant boyama işlemimiz sprey boya ile değil, profesyonel fırın boya ve vernikleme  sistemiyle yapılır. Zemin hazırlığı ve astar uygulaması doğru yapıldığı için, tazyikli suya, balata tozuna ve yol şartlarına karşı fabrika çıkışı jantlar kadar dayanıklıdır.', NULL, 1, '2025-11-25 21:56:03', '2025-11-25 21:56:03'),
	(7, 'Oto yıkamada fırça kullanıyor musunuz?', 'Kesinlikle hayır. Adana\'da araçlarda oluşan çiziklerin (harelerin) en büyük sebebi fırçalı yıkamadır. Sirius Oto Design\'da tüm yıkama işlemleri yumuşak mikrofiber eldivenler ve basınçlı su ile "Fırçasız" olarak yapılır. Kullandığımız şampuanlar boyaya zarar vermeyen pH dengeli ürünlerdir.', NULL, 1, '2025-11-25 21:56:25', '2025-11-25 21:56:25'),
	(8, 'Aracımda güneş yanığı veya derin çizikler var, düzelir mi?', 'Vernik tabakasının altına inmiş derin çizikler veya verniği tamamen kavlamış güneş yanıkları için boya koruma/pasta cila işlemleri çözüm olmayabilir; bu durumlarda boyama gerekebilir. Ancak vernik üzerindeki kılcal çizikler, matlaşmalar ve hafif oksitlenmeler, profesyonel polisaj (pasta-cila) işlemlerimizle %90 oranında giderilerek eski parlaklığına kavuşturulur. Ücretsiz ekspertiz için atölyemize bekleriz.', NULL, 1, '2025-11-25 21:56:51', '2025-11-25 21:56:51'),
	(9, 'Koltuk yıkama sonrası "kusma" (lekenin geri gelmesi) olur mu?', 'Kusma sorunu, koltuğun yüzeyinin silinip süngerin içindeki kirin çekilmemesi sonucu oluşur. Biz profesyonel vakumlu makineler kullanarak kiri süngerin derinliklerinden çekeriz. Ayrıca doğru kurutma teknikleri uyguladığımız için işlem sonrası leke tekrar yüzeye çıkmaz ve koku yapmaz.', NULL, 1, '2025-11-25 21:57:16', '2025-11-25 21:57:16'),
	(10, 'Fiyatlarınız neye göre belirleniyor?', 'Hizmet fiyatlarımız; aracın boyutuna (Binek, SUV, Ticari), boyanın mevcut durumuna (çizik yoğunluğu) ve tercih edilecek malzeme kalitesine (Örn: 3 Yıllık Seramik vs 5 Yıllık Seramik) göre değişiklik gösterir. En doğru fiyatı verebilmemiz ve aracınızın ihtiyacını belirleyebilmemiz için sizi kahve içmeye iş yerimize davet ediyoruz.', NULL, 1, '2025-11-25 21:57:56', '2025-11-25 21:57:56');

