INSERT INTO `our_values` (`id`, `title`, `description`, `created_at`, `updated_at`) VALUES
	(2, 'Profesyonel Ekip', 'Sıradan oto yıkama personeliyle değil; car detailing, seramik kaplama ve boya koruma alanında eğitimli, sertifikalı ve tecrübeli ustalarla çalışıyoruz. Adana’da aracınızı, boya teknolojisinden ve kimyasal bileşenlerden anlayan gerçek profesyonellere emanet etmenin huzurunu yaşayın.', '2025-11-26 17:27:46', '2025-11-26 17:27:46'),
	(3, 'Dünya Standartlarında Malzeme Kalitesi', 'Ucuz ve merdiven altı ürünleri atölyemize sokmuyoruz. Uygulamalarımızda; boyanıza zarar vermeyen pH dengeli şampuanlar, 9H sertlikte sertifikalı seramikler ve sararma yapmayan TPU esaslı PPF filmler kullanıyoruz. Kalitesi dünya çapında kanıtlanmış markalarla aracınızın değerini koruyoruz.', '2025-11-26 17:27:59', '2025-11-26 17:27:59'),
	(4, 'Araca ve İhtiyaca Özel Butik Çözümler', 'Fabrikasyon bir üretim hattı gibi çalışmıyoruz. Her aracın boya durumu, kullanım alışkanlıkları ve müşterimizin beklentisi farklıdır. Ücretsiz analiz yapıyor, ezbere paketler yerine aracınızın ihtiyacına ve bütçenize en uygun \'kişiye özel\' bakım reçetesini sunuyoruz.', '2025-11-26 17:28:19', '2025-11-26 17:28:19'),
	(5, '%100 Müşteri Memnuniyeti Garantisi', 'Bizim için işlem, ödeme aldığımızda değil; siz aracınızı görüp gülümsediğinizde biter. Satış sonrası destek ve uygulama garantilerimizle, Adana’nın en güvenilir araç bakım merkezi olarak her zaman arkanızdayız. Memnuniyetsizlik durumunda telafi garantisi sunuyoruz.', '2025-11-26 17:29:32', '2025-11-26 17:29:32'),
	(6, 'Randevulu Sistem ve Zamanında Teslimat', 'Vaktinizin ne kadar değerli olduğunu biliyoruz. \'Yarın gel\', \'akşama kalır\' gibi belirsizliklere yer yok. Randevu sistemimize sadık kalarak, aracınızı söz verdiğimiz saatte ve dakikada, tam da beklediğiniz kondisyonda size teslim ediyoruz.', '2025-11-26 17:29:51', '2025-11-26 17:29:51'),
	(7, 'İşimiz Sadece Meslek Değil, Bir Tutku', 'Otomobilleri sadece metal yığınları olarak görmüyoruz. Ekibimizdeki herkes gerçek birer otomobil tutkunu. Detaylara gösterdiğimiz bu aşırı özen, işimizi severek yapmamızdan kaynaklanıyor. Aracınıza kendi aracımızmış gibi hassasiyetle yaklaşıyoruz.', '2025-11-26 17:30:04', '2025-11-26 17:30:04');

