INSERT INTO `seos` (`id`, `title`, `description`, `canonical`, `page_id`, `created_at`, `updated_at`) VALUES
	(1, 'Car Detailing', 'Adana car detailing ve detaylı temizlik hizmetiyle aracınız ilk günkü hijyenine kavuşsun. Profesyonel iç-dış bakım Sirius\'ta.', 3, 3, '2025-11-24 21:15:34', '2025-11-24 21:15:34'),
	(2, 'Hakkımızda', 'Adana\'da car detailing, seramik kaplama ve oto bakım uzmanı Sirius Car Design ile aracınızın değerini koruyun.', 1, 1, '2025-11-24 21:19:21', '2025-11-24 21:19:21'),
	(3, 'Araç Koruma ve Detailing', 'Adana\'da car detailing, seramik kaplama, PPF ve oto yıkama hizmetleri. Profesyonel araç koruma ve bakım çözümlerimiz', 2, 2, '2025-11-24 21:24:22', '2025-11-24 21:24:22'),
	(4, 'Adana Boya Koruma ve Parlatma', 'Adana\'da profesyonel boya koruma, hare giderme ve pasta cila hizmeti. Aracınızı güneş yanığına ve çiziklere karşı koruyun, parlaklığını sabitleyin.', 5, 5, '2025-11-25 18:12:26', '2025-11-25 18:12:26'),
	(5, 'PPF Kaplama ve Boya Koruma Filmi', 'Adana PPF kaplama hizmeti. Aracınızı taş vurukları ve çiziklerden koruyan TPU esaslı, kendini yenileyen şeffaf boya koruma filmi Sirius kalitesiyle.', 7, 9, '2025-11-25 20:36:12', '2025-11-25 20:36:21'),
	(6, 'Uygulama Galerisi ve Referanslar', 'Adana car detailing, seramik kaplama ve PPF uygulama fotoğraflarımız. Ustalık, ekip çalışması ve öncesi-sonrası kareleri galerimizde inceleyin.', 4, 4, '2025-11-25 20:37:25', '2025-11-25 20:37:25'),
	(7, 'Jant Boyama ve Renk Değişimi', 'Adana profesyonel jant boyama ve kaliper boyama hizmeti. Çelik jantlarınızı çiziklerden arındırın, özel renk seçenekleriyle aracınızı yenileyin.', 8, 8, '2025-11-25 20:39:49', '2025-11-25 20:39:49'),
	(8, 'Seramik Kaplama ve Boya Koruma', 'Adana 9H seramik kaplama ve boya koruma merkezi. Nano teknolojik parlaklık, çizik giderme ve su itici yüzey koruma hizmetlerimizle tanışın.', 6, 6, '2025-11-25 20:41:13', '2025-11-25 20:41:13'),
	(9, 'Detaylı Temizlik ve Oto Kuaför', 'Adana araç içi detaylı temizlik ve sterilizasyon hizmeti. Koltuk, tavan ve taban halısında buharlı temizlik ve leke çıkarma Sirius kalitesiyle.', 7, 7, '2025-11-25 20:43:08', '2025-11-25 20:43:08'),
	(10, 'Değerlerimiz ve Hizmet Anlayışımız', 'Adana\'da kalite, güven ve tutkuyla sunduğumuz araç bakım hizmetlerinin arkasındaki temel prensiplerimiz. Müşteri memnuniyeti ve uzmanlık ilkelerimiz.', 18, 18, '2025-11-26 17:58:53', '2025-11-26 17:58:53'),
	(11, 'Oto Bakım Blogu ve Haberler', 'Adana araç bakım rehberi, detailing ipuçları, seramik kaplama ve PPF hakkında teknik bilgiler. Sektörel haberler ve uzman tavsiyeleri blogumuzda.', 10, 10, '2025-11-26 18:00:19', '2025-11-26 18:00:19'),
	(12, 'İletişim ve Randevu Al', 'Adana car detailing, seramik kaplama ve boya koruma fiyatları için bizimle iletişime geçin. Adres, telefon, WhatsApp ve yol tarifi bilgileri.', 16, 16, '2025-11-26 18:00:37', '2025-11-26 18:00:37'),
	(13, 'Sıkça Sorulan Sorular', 'Seramik kaplama ömrü, PPF ile kaplama farkı ve detaylı temizlik hakkında merak edilenler. Adana araç bakım uzmanı Sirius\'tan en çok sorulanlar.', 15, 15, '2025-11-26 18:00:52', '2025-11-26 18:00:52');

